import React, { useState } from 'react';

import ApiCalendar from 'react-google-calendar-api';
import moment from 'moment';
import DatePicker from 'react-date-picker';

function App() {
  const [auth, setAuth] = useState(false);
  const [processed, setProcessed] = useState(false);
  const [data, setData] = useState(false);

  const handleSignIn = (e) => {
    ApiCalendar.handleAuthClick();
    if (ApiCalendar.sign) {
      setAuth(true);
      console.log(auth);
    }
  }

  const handleSignOut = (e) => {
    ApiCalendar.handleSignoutClick();
    setAuth(false);
    setProcessed(false);
    console.log(auth);
  }

  const getEvents = async () => {
    while (!ApiCalendar.sign) {
      console.log('waiting to sign in');
    }
    const { result } = await ApiCalendar.listEvents({
      timeMin: moment().subtract(3, 'days').toISOString(),
      timeMax: moment().toISOString(),
      showDeleted: true,
      maxResults: 15,
      orderBy: 'updated'
    });
    setData(result.items);
    setProcessed(true);
  }

  const displayEvents = () => {
    data.forEach(item => {
      console.log(item.summary);
    })
    const listItems = data.map((item) => 
      <li>{item.summary}</li>
    );
    console.log(listItems);
    return (
      <ul>{listItems}</ul>
    )
  }

  return (
    <div className="App">
      {!auth ? 
        <button onClick={(e) => handleSignIn(e)}>Sign In</button> : 
        <>
          <button onClick={(e) => handleSignOut(e)}>Sign Out</button>
          <button onClick={(e) => getEvents()}>Process Calendar Data</button>
        </>
      }
      {processed ? displayEvents(): <p></p>}
    </div>
  );
}

export default App;
